import { getPosts } from "../actions/posts";

const reducer = (posts, getPosts) => {
    switch (getPosts.type) {
        case 'FETCH_ALL':
            return getPosts.payload;
        case 'CREATE':
            return posts;
        default:
            return posts;
    }
};

export default reducer;
